mapper.select:
SELECT QNA_NO, QNA_WRITER, QNA_CONTENT, DATE_FORMAT(DATE_ADD(QNA_INSERTDATE, INTERVAL 9 HOUR), '%Y-%m-%d') AS QNA_INSERTDATE, QNA_COMMENT FROM QNA_LIST WHERE QNA_NO > 0
ORDER BY QNA_NO DESC
LIMIT #{pageStart}, #{perPageNum}

mapper.update:
UPDATE 
					QNA_LIST 
SET 
					QNA_CONTENT = #{qna_content}
WHERE 
					QNA_NO = #{qna_no}

UPDATE 
					QNA_LIST 
SET 
					QNA_COMMENT = #{qna_comment} 
WHERE 
					QNA_NO = #{qna_no}

UPDATE 
					QNA_LIST 
SET 
					QNA_COMMENT = #{qna_comment} 
WHERE 
					QNA_NO = #{qna_no}
